// Dependencies
import axios from "axios";
import { properties } from "../../config/properties";

class PlayerApiGenerated {

  static contextUrl = properties.endpoint + "/player";

  // CRUD METHODS

  /**
  * PlayerService.create
  *   @description CRUD ACTION create
  *
  */
  static createPlayer(player) {
    return axios.post(PlayerApiGenerated.contextUrl, player )
      .then(response => {
        return response.data;
      })
      .catch(error => {
        throw error;
      });
  }

  /**
  * PlayerService.delete
  *   @description CRUD ACTION delete
  *   @param ObjectId id Id
  *
  */
  static deletePlayer(id) {
    return axios.delete(PlayerApiGenerated.contextUrl + "/" + id)
      .then(response => {
        return response.data;
      })
      .catch(error => {
        throw error;
      });
  }

  /**
  * PlayerService.get
  *   @description CRUD ACTION get
  *   @param ObjectId id Id resource
  *
  */
  static getOnePlayer(id) {
    return axios.get(PlayerApiGenerated.contextUrl + "/" + id)
      .then(response => {
        return response.data;
      })
      .catch(error => {
        throw error;
      });
  }

  /**
  * PlayerService.list
  *   @description CRUD ACTION list
  *
  */
  static getPlayerList() {
    return axios.get(PlayerApiGenerated.contextUrl)
      .then(response => {
        return response.data;
      })
      .catch(error => {
        throw error;
      });
  }

  /**
  * PlayerService.update
  *   @description CRUD ACTION update
  *   @param ObjectId id Id
  *
  */
  static savePlayer(player) {
    return axios.post(PlayerApiGenerated.contextUrl + "/" + player._id, player )
      .then(response => {
        return response.data;
      })
      .catch(error => {
        throw error;
      });
  }



    // Custom APIs
  /**
  * PlayerService.KnockOut
  *   @param String PlayerToKnockout
  *   @returns Boolean
  *
  */
  static KnockOut(...params) {
    return axios.patch(PlayerApiGenerated.contextUrl + '/url/{id}' )
      .then(response => {
        return response.data;
      })
      .catch(error => {
        throw error;
      });
  }

}

export default PlayerApiGenerated;
